module.function:
this.pinsSimulator = shell.delegate("addSimulatorPart", {
	header : { 
		label : "Accelerometer", 
		name : "3 Analog Inputs", 
		iconVariant : PinsSimulators.SENSOR_MODULE 
	},
	axes : [
		new PinsSimulators.AnalogInputAxisDescription(
			{
				valueLabel : "X",
				valueID : "x",
				speed : 0
			}
		),
		new PinsSimulators.AnalogInputAxisDescription(
			{
				valueLabel : "Y",
				valueID : "y",
				speed : 0.05
			}
		),
		new PinsSimulators.AnalogInputAxisDescription(
			{
				valueLabel : "Z",
				valueID : "z",
				speed : 0.05
			}
		),
	]
});

module.script:
exports.pins = {
	x: { type: "A2D" },
	y: { type: "A2D" },
	z: { type: "A2D" }
};
