text.behavior:
function onCreate(text, data) {
	var linkBehavior = new LinkBehavior;
	linkBehavior.uri = data.uri;
	var format = [{
		style: copyrightStyleBoldLeft, spans: [
			data.string,
			{style: copyrightStyleBoldSilver, string: data.uri, behavior: linkBehavior}
		]
	}];
	text.format(format);
}

